// Creature class
// Create a "bloop" creature
// NOTE: a creature is an environment


class Bloop {
  constructor(dna_, health) {
    // features
    this.name = null
    this.health = health
    this.dna = dna_
    this.attractions = [Math.random()]
    this.phenotype = { r: 0, g: 0, b: Math.round(this.attractions[0] * 100) }
    this.address = null

    // spaces
    // generated by the attached modules
    this.action_space = 0
    this.observation_space = 0

    // modules
    this.slots = 4
    this.modules = []

    // state
    this.observations = []
    this.action = 0
    this.state = {}
  }

  spin(action) {
    this.act(action)
  }

  act(action) {
    if (action && action !== 0) {
      // TODO: translate action called from agent into action by module
      // this.action = this.modules[this.translate(action)].spin(this)
    }
  }

  reset() {
    this.action = 0
    this.action_space = this.modules.length
    this.observations = []
  }

}

module.exports = { Bloop }